INSERT INTO `art_edtmsg` VALUES ('3', '0', 'images/703d87be-0092-4ff7-8fe1-487ccaecdf56.jpg', '靳埭强设计奖2010全球华人大学生设计大赛获奖作品巡回展郑州站展览在我院举行', null, 'http://art.demo.com/Home/Article/notice.html?aid=35', '2016-05-28 13:26:17.000000');
INSERT INTO `art_edtmsg` VALUES ('4', '0', 'images/618c62ef-65e4-425d-8f3f-3474e5675adb.jpg', '艺术设计学院动画系特邀知名漫画家张元英先生莅临我院讲学', '应艺术设计学院动画专业的邀请，国内知名漫画家张元英先生于5月3日上午在西五楼514教室，为我院动画专业的学生进行了一场 “如何成为一名合格的漫画家”的学术讲座。', 'http://art.demo.com/Home/Article/dynamic.html?aid=26', '2016-05-29 09:36:52.000000');
